import React, {useState} from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Radio from '@material-ui/core/Radio';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControl from '@material-ui/core/FormControl';
import axios from 'axios';
import { hostname } from './Connect';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import DeleteIcon from '@material-ui/icons/Delete';
const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
    },
  },
}));

export default function User() {
  const classes = useStyles();
  const [showModal , setShowModal] = useState(false)
  const [user , setUser] = useState(false)
  const [level , setLevel] = useState("admin")
  const handleClose = () => {
   setShowModal(false);
  };

  const handleChangeReserveData=(e)=>{
   setUser({...user , [e.target.name]: e.target.value})
  }

  const handleChange = (e) => {
   setLevel(e.target.value);
  };

  const SaveUser = async() =>{
   let url = hostname + '/user/save_user'
   let rs = await axios.post(url,{
    name : user.name , 
    username : user.username,
    password : user.password,
    level : level
   })
   if(rs.data.message == 'Save Success'){

   }
   console.log(rs.data)
   // console.log(data)
  }

  return (
   <>
    <div className={classes.root}>
      <h3>User / Admin</h3>
      <Button onClick={() => setShowModal(true)} style={{width : "15%"}} variant="contained" color="primary">
        เพิ่ม User
      </Button>
    </div>
    <Dialog
        open={showModal}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
    >
     <DialogTitle id="alert-dialog-title">{"User Account"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
          <form className={classes.form} noValidate>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="name"
              label="ชื่อ"
              name="name"
              autoFocus
              onChange={handleChangeReserveData}
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="Username"
              label="Username"
              name="username"
              autoFocus
              onChange={handleChangeReserveData}
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              onChange={handleChangeReserveData}
            />
             <div>
              <FormControl>
                <RadioGroup aria-label="quiz" name="quiz" value={level} onChange={handleChange}>
                  <FormControlLabel value="admin" control={<Radio />} label="Admin" />
                  <FormControlLabel value="user" control={<Radio />} label="User" />
                </RadioGroup>
              </FormControl>
            </div>
          </form>
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={()=>{SaveUser()}} color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>
   </>
  );
}
